function countdownToBirthday(){const NOW=(new Date).getTime();const BIRTHDAY_DATE=new Date("12.19.2026 24:00:00").getTime();const TIME_LEFT=BIRTHDAY_DATE-NOW;if(TIME_LEFT<0){countdownToBirthdayCompleted()}const DAYS=Math.floor(TIME_LEFT/(1e3*60*60*24));const HOURS=Math.floor(TIME_LEFT%(1e3*60*60*24)/(1e3*60*60));const MINUTES=Math.floor(TIME_LEFT%(1e3*60*60)/(1e3*60));const SECONDS=Math.floor(TIME_LEFT%(1e3*60)/1e3);document.getElementById("days").innerHTML=DAYS;document.getElementById("hours").innerHTML=HOURS;document.getElementById("minutes").innerHTML=MINUTES;document.getElementById("seconds").innerHTML=SECONDS}function countdownToBirthdayCompleted(){clearInterval(countdownToBirthday);document.getElementById("birthday-countdown").style.display="none";return}countdownToBirthday();setInterval(countdownToBirthday,1e3);